//BRUNO GUILHERME LUNARDI//RUAN MICHEL ADABO//IAN MARCELO TOBARpackage controle;import java.util.*;import controle.*;import limite.*;import modelo.*;public class ControleImovel {//abre classe ControleImovel    public ControlePrincipal ctrPrincipal;        public LimiteImovel lmtImovel;    private ArrayList<Imovel> listaImoveis = new ArrayList<Imovel>();    //construtor 01    public ControleImovel(ControlePrincipal ctrPrincipal) {//abre construtor 01        this.ctrPrincipal = ctrPrincipal;        lmtImovel = new LimiteImovel(this);    }//fecha construtor 01    public void cadastraImovel(int codigo, String tipo, String descricao, String nomeProprietario, double precoSolicitado, String data) {        listaImoveis.add(new Imovel(codigo, tipo, descricao, nomeProprietario, precoSolicitado, data));    }    public ArrayList<String> listarImoveis() {        ArrayList<String> lista = new ArrayList<String>();        String aux;        for (Imovel i : listaImoveis) {            aux = "Codigo: " + i.getCodigo() + "\nTipo: " + i.getTipo() + "\nDescrição: " + i.getDescricao() + "\nNome Do Proprietário: "                    + i.getNomeVendedor() + "\nPreço: " + i.getPrecoSolicitado() + "\nData de Cadastro: " + i.getData();            lista.add(aux);        }        return lista;    }    public ArrayList<Imovel> getLista() {        return listaImoveis;    }    public void editaLista(int index, Imovel i) {        listaImoveis.remove(index);        listaImoveis.add(index, i);    }    public void removeLista(int index) {        listaImoveis.remove(index);    }}//fecha classe ControleImovel